package harshita02_03;

public class DataTypeExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
		Data type :- numeric, string, point, true-false

		1) primitive :- c/c++ adopt, size fixed
		2) non-primitive :- java build

		primitive data type :- int,char,float,double,boolean,short,long,byte
*/

		//primitive data type....
		int a = 10;
		
		char c = 'a';
		
		float f = 12.23f;
		
		double d = 1223.3434;
		
		boolean b = true; //false
		
		short s = 12;
		
		long l = 12345678;
		
		byte by = 12;
		
		System.out.println("a = "+ a);
		System.out.println("c = "+ c);
		System.out.println("f = "+ f);
		System.out.println("d = "+ d);
		System.out.println("b = "+ b);
		System.out.println("s = "+ s);
		System.out.println("l = "+ l);
		System.out.println("by = "+ by);
		
		//add,sub,multi,div,modulus,SI
		
		
	}

}
